command:example.dev:code:
[CODE]
Exit-OnCtrlC
while($true)
{
  [CODE]
  [Schleife-Anfang]
    Exit-OnCtrlC
    [CODE]
    Exit-OnCtrlC
  [Schleife-Ende]
  break
}
Trap [ExecutionEngineException]{
  Write-LogMessage -level High -CorrelationId $scriptCorrId -message "Script terminated by Ctrl-C."
  $global:scriptaborted = $true
  #region Auszuführender Code nach manuellem Abbruch durch Ctrl-C
  if(!$DoNotDisplayConsole){
    Show-TextLine -text "Script wurde durch Ctrl-C abgebrochen!" -fgColor Red -bgColor White
    Wait-ForKey
  }
  continue
  #endregion
}

[CODE]
try
{
  [CODE]
}
catch
{
  $exMessage = $_.Exception.Message
  $innerException = $_.Exception.InnerException
  $info = "Fehler bei Prüfung xy"
  Push-Exception -list $list -web $web -site $site -exMessage $exMessage -innerException $innerException -info $info
}
